import pandas as pd


def complete_features(df:pd.DataFrame):
    # Ensure that all the necessary columns are present, even if they are missing from the input
    for col in FEATURE_COLS:
        if col not in df.columns:
            df[col] = 0  # Add missing columns with 0 values

    # Reorder the columns to match FEATURE_COLS
    df_input_encoded = df[FEATURE_COLS]
    return df_input_encoded
